mapper.update:
update t_cooperation_debt
accountspayable=#{accountspayable}, assetall=#{assetall}, bankloan=#{bankloan}, buildingproject=#{buildingproject}, businessprofit=#{businessprofit}, capitalreserve=#{capitalreserve}, cashcapital=#{cashcapital}, cooperationname=#{cooperationname}, depositreceived=#{depositreceived}, deprecication=#{deprecication}, financialcost=#{financialcost}, fixedasset=#{fixedasset}, fixedassetall=#{fixedassetall}, floatasset=#{floatasset}, floatassetall=#{floatassetall}, floatdebt=#{floatdebt}, floatdebtall=#{floatdebtall}, handingcost=#{handingcost}, interestexpense=#{interestexpense}, inventory=#{inventory}, investincome=#{investincome}, invisibleasset=#{invisibleasset}, longdebtall=#{longdebtall}, netprofit=#{netprofit}, netvalue=#{netvalue}, organcode=#{organcode}, paidaddedtax=#{paidaddedtax}, payableaddedtax=#{payableaddedtax}, payableincometax=#{payableincometax}, prepayment=#{prepayment}, profitloss=#{profitloss}, realcapital=#{realcapital}, receivables=#{receivables}, recorder=#{recorder}, recordtime=#{recordtime}, registercapital=#{registercapital}, shortinvest=#{shortinvest}, shortloan=#{shortloan}, subsidyincome=#{subsidyincome}, surplusreserve=#{surplusreserve}, totalequity=#{totalequity}, totalequity1=#{totalequity1}, totalprofit=#{totalprofit}, unallotprofit=#{unallotprofit}, unbusinessexpense=#{unbusinessexpense}, unbusinessincome=#{unbusinessincome}, unpaidtax=#{unpaidtax},
where debtid=#{debtid,jdbcType=INTEGER}

mapper.select:
SELECT t.* FROM t_cooperation_debt t, (select debtid, ROW_NUMBER() OVER () as rn from t_cooperation_debt) AS t1 where t1.debtid= t.debtid
and t.recorder like '%${recorder}%'
and t.cooperationname like '%${cooperationname}%'
and t.organcode like '%${organcode}%'
and t.recordtime >=to_date('${recordTimeBegin}','yyyy-mm-dd')
and t.recordtime <=to_date('${recordTimeEnd}','yyyy-mm-dd')
and t1.rn > #{start}
and t1.rn <= #{end}
order by ${sortField} ${sortOrder}
order by t.debtid desc
